import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

// 초기값

const initialState = {
  posts: [],
  isLoading: false,
  error: null,
};
export const __getPosts = createAsyncThunk(
  "posts/getPosts",
  async (payload, thunkAPI) => {
    try {
      const data = await axios.get("http:localhost:3001/posts");
      return thunkAPI.fulfillWithValue(data.data);
    } catch (error) {
      return thunkAPI.rejectWithValue(error);
    }
  }
);


export const formSlice = createSlice({
  name : "form",
  initialState,
  reducers: { // Reducer 안에 만든 함수 자체가 리듀서 로직이자, Action creator
    addPost: (state, action) => {
      const addPost = [...state, action.payload];
      return addPost;     
    },

  extraReducers: {
    [__getTodos.pending]: (state, action) => {
      console.log("pending 상태", state, action); // Promise가 pending일때 dispatch
    },
    [__getTodos.fulfilled]: (state, action) => {
      console.log("fulfilled 상태", state, action); // Promise가 fullfilled일 때 dispatch
    },
    [__getTodos.rejected]: (state, action) => {
      console.log("rejected 상태", state, action); // Promise가 rejected일 때 dispatch
    },
  },

  }
});


export const { addPost } = formSlice.actions;
export default formSlice.reducer;
