import React from "react";
import styled from "styled-components";
import { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux/";
import { createComment } from "../redux/modules/comment";
import { useParams } from "react-router-dom";
import { __getComments } from "../redux/modules/comment";
import axios from "axios";
import { toBeInTheDocument } from "@testing-library/jest-dom/dist/matchers";

let number = 2;

const Comment = () => {

  const {id} = useParams();
  const dispatch = useDispatch();

  const { isLoading, error, comments } = useSelector((state) => state.comment);


  const filteredComment = comments.filter((comment) => comment.parentId == id);
  console.log(comments)

  const initialState = {
    parentId: id,
    id: 0,
    desc: "",
  };

  const [comment, setComment] = useState(initialState);
  const [targetId, setTargetId] = useState(null);
  const [editComment, setEditComment] = useState({
    title: "",
  });

  const onChangeHandler = (event) => {
    const { name, value } = event.target;
    setComment({ ...comment, [name]: value, id: number });
  };

  const onSubmitHandler = (event) => {
    event.preventDefault();
    dispatch(createComment({ ...comment, id: number }));
    axios.post("http://localhost:3001/comments", { ...comment, id: number });
    setComment(initialState);
    number++;
  };

  //comment 삭제
  const onClickDeleteButtonHandler = (commentId) => {
    axios.delete(`http://localhost:3001/comments/${commentId}`);
  };

  // comment 수정
  const onClickEditButtonHandler = (commentId, edit) => {
    axios.patch(`http://localhost:3001/todos/${commentId}`, edit);
  };

  useEffect(() => {
    dispatch(__getComments());
  }, [dispatch]);

  if (isLoading) {
    return <div>로딩 중...</div>;
  }

  if (error) {
    return <div>{error.message}</div>;
  }

  return (
    <StComment>
      <form onSubmit={onSubmitHandler}>
        <label>댓글</label>
        <input
          type="text"
          name="desc"
          value={comment.desc}
          onChange={onChangeHandler}
        />
        <button>입력하기</button>
      </form>

      <div>
        {filteredComment.map((comment) => {
          return (
            <div key={comment.id}>
              <p>{comment.desc}</p>             
              <button>수정</button>
              <button
              type="button"
              onClick={() => onClickDeleteButtonHandler(comment.id)}
            >
              삭제하기
            </button>
            </div>
          );
        })}
      </div>
    </StComment>
  );
};

export default Comment;

const StComment = styled.div`
  width: 40%;
`;
