//   width: 100%;
//   max-width: 1200px;
//   min-width: 800px;
//   height: 800px;
// `;
// const StButtonForm = styled.button`
//   border: 1px solid ${({ borderColor }) => borderColor};
//   cursor: pointer;
// `;
export default Form;
