import React from "react";
// import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { useState } from "react";
import { addPost } from "../redux/modules/form";


let number = 1;
const Form = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();


  const initialState = {
    id: 0,
    username: "",
    category:"",
    title: "",
    writer: "",
    body: "",
  };

  const [post, setPost] = useState(initialState);

  const onChangeHandler = (event) => {
    const { name, value } = event.target;
    setPost({...post, [name]: value, id: number});
  };

  const onSubmitHandler = (event) => {
    event.preventDefault();
    dispatch(addPost({...post, id: number}));
    setPost(initialState);
    number++;
    // navigate('/')
  };

  const OPTIONS = [
    { value: "apple", name: "사과" },
    { value: "banana", name: "바나나" },
    { value: "orange", name: "오렌지" },
  ];


  const handleSelect = (event) => {
    setSelected(event.target)
  };
  console.log(selected)

  const [imageSrc, setImageSrc] = useState("");

  const imageUpload = (fileBlob) => {  

    const reader = new FileReader();
    reader.readAsDataURL(fileBlob);

    return new Promise((resolve) => {
      reader.onload = () => {
        setImageSrc(reader.result);
        resolve();
      };
    });
  };


  return (
    <form onSubmit={onSubmitHandler}>
      <div>
        <div>
          <label>작성자</label>
          <input 
          type="text"
          name="username"
          value={post.username}
          onChange={onChangeHandler} 
          />
        </div>
        <div>
          <label>카테고리</label>
          <SelectBox options={OPTIONS}></SelectBox>;
        </div>
        <div>
          <label>작가명</label>
          <input 
          type="text"
          name="writer"
          value={post.writer}
          onChange={onChangeHandler} 
          />
        </div>
        <div>
          <label>책 제목</label>
          <input 
          type="text"
          name="title"
          value={post.title}
          onChange={onChangeHandler} 
          />
        </div>
        <div>
          <label>내용</label>
          <input 
          type="text"
          name="body"
          value={post.body}
          onChange={onChangeHandler} 
          />
        </div>      
        <div>
          </div>
            <p>이미지를 업로드해주세요</p>
            <input type="file" accept="image/*" onChange={(e) => {
        imageUpload(e.target.files[0]); }}/>
            <div className="preview">
                    {imageSrc && <img src={imageSrc} alt="preview-img" width="200px" height="250px" />}
            </div>
          <div>
            <br></br>
          <div>
            <button onClick={() => { navigate('/') }}>취소</button> 
            <button>추가하기</button>
            <button onClick={() => setPost(initialState) }>다시 작성하기</button>
          </div>
        </div>
      </div>
    </form>
  );
};
const OPTIONS = [
	{ value: "apple", name: "사과" },
	{ value: "banana", name: "바나나" },
	{ value: "orange", name: "오렌지" },
];
const SelectBox = (props) => {
	return (
		<select>
			{props.options.map((option) => (
				<option
					key={option.value}
					value={option.value}
				>
					{option.name}
				</option>
			))}
		</select>
	);
};


export default Form;
